import React from 'react';

class Games extends React.Component{
    constructor(props){
        super(props);
        this.state = {objs:this.props.objs,objs2:[],shop:0,stock:1,total:0}
    }

    changeState(e){
        if(e==='All'){
            this.setState({objs:this.props.objs})
        }else{
            var T = this.props.objs.filter((ee)=>ee.cate===e)
            this.setState({objs:T})
        }
    }

    AddToCart(e){
        var T = this.state.objs2;
        var some = 0;
        this.state.objs.find((ee)=>ee.ref===e.target.id && T.push(ee))
        this.state.objs2.map((ee)=>{some=some+ee.price})
        this.setState({objs2:T,total:some})
    }
    
    Undo(e){
        var some = this.state.total;
        this.state.objs2.map((ee)=>{
            if(ee.ref===e.target.id){
                some=some-ee.price
            }
        })
        this.setState({objs2:this.state.objs2.filter((ee)=>ee.ref!==e.target.id),total:some})
    }

    ShopCard(){
        return(
            <div className='shopCardP'>
                <div className='closeP' onClick={()=>{this.setState({shop:0})}}>
                    <img src="icon/close.png" alt=''/>
                </div>
                <div className='text-light'>
                    Total : {this.state.total}$
                </div>
                <div className='shopCard'>
                    {this.state.objs2.map((e,i)=>
                    <div key={i} className='py-2 d-flex align-items-center justify-centent-evenly'>
                        <img style={{height:'20%'}} className='col-3' src={e.url} alt=''/>
                        <div style={{backgroundColor:'#1B1C20'}} className='col-9 py-3'>
                            <p className='text-center text-light'>{e.name}</p>
                            <p className='text-center text-light'>{e.price}$</p>
                            <input id={e.ref} onClick={(e)=>{this.Undo(e)}} value='UNDO ' type='button' className='btn btn-danger text-light col-6 d-block mx-auto'/>
                        </div>
                    </div>
                    )}
                    {this.state.total}
                    <input value='Buy' type='button' className='mt-3 col-8 d-block mx-auto btn btn-success'/>
                </div>
            </div>
        )
    }

    render(){
        return(
            <div className='container d-flex flex-column align-items-center mt-4'>
                <header className='d-flex col-12 my-1 align-items-center justify-content-between py-3'>
                    {this.props.nav.map((e,i)=>
                        <input onClick={()=>this.changeState(e)} key={i} value={e} type='button' className='btn btn-outline-light col-2'/>
                    )}
                    <img onClick={()=>{this.setState({shop:1})}} style={{height:'40px',cursor:'pointer'}} src='icon/SC.png' alt=''/>
                </header>
                <div style={{backgroundColor:'#0D0C11'}} className='mt-2 col-11 d-flex justify-content-evenly flex-wrap'>
                    {this.state.objs.map((e,i)=>
                        <div key={i} className='cadre py-3 col-3 d-flex flex-column align-items-center justify-centent-center'>
                            <img style={{height:'320px'}} className='col-9' src={e.url} alt=''/>
                            <div style={{backgroundColor:'#1B1C20'}} className='col-9 py-3'>
                                <p className='text-center text-light'>{e.name}</p>
                                <p className='text-center text-light'>{e.price}$</p>
                                <input id={e.ref} onClick={(e)=>{this.AddToCart(e)}} value='ADD To Cart' type='button' className='col-6 d-block mx-auto btn btn-primary'/>
                            </div>
                        </div>
                    )}
                </div>
                {this.state.shop===1 && (this.state.stock===1 && this.ShopCard())}
            </div>
    )}
}
export default Games;
