import React from 'react';

class ShopCard extends React.Component{
    constructor(props){
        super(props);
        this.state = {objs2: []};
    }

    Undo(e){
        this.setState({objs2:this.state.objs2.filter((ee)=>ee.ref!==e.ref),total:this.state.total-e.price})
    }
    render(){
        return(
            <div className='shopCardP'>
                <div className='closeP' onClick={()=>{this.props.se}}>
                    <img src="icon/close.png" alt=''/>
                </div>
                <div className='text-light'>
                    Total : {this.state.total}$
                </div>
                <div className='shopCard'>
                    {this.state.objs2.map((e,i)=>
                    <div key={i} className='py-2 d-flex align-items-center justify-centent-evenly'>
                        <img style={{height:'20%'}} className='col-3' src={e.url} alt=''/>
                        <div style={{backgroundColor:'#1B1C20'}} className='col-9 py-3'>
                            <p className='text-center text-light'>{e.name}</p>
                            <p className='text-center text-light'>{e.price}$</p>
                            <input onClick={()=>{this.Undo(e)}} value='UNDO ' type='button' className='btn btn-danger text-light col-6 d-block mx-auto'/>
                        </div>
                    </div>
                    )}
                    {this.state.total>0 && <input value='Buy' type='button' className='mt-3 col-8 d-block mx-auto btn btn-success'/>}
                </div>
            </div>
        )
    }
}

export default ShopCard
