import React from 'react';

class Games extends React.Component{
    constructor(props){
        super(props);
        this.state = {objs:this.props.objs,objs2:[],shop:0,stock:0,total:0}
    }

    Search(e){
        if(e.target.value.trim()!==''){
            this.setState({objs:this.props.objs.filter((obj)=>obj.name.toLowerCase().includes(e.target.value.toLowerCase()))});
        }else{
            this.setState({objs:this.props.objs});
        }
    }

    changeState(e){
        e==='All'?this.setState({objs:this.props.objs}):this.setState({objs:this.props.objs.filter((ee)=>ee.cate===e)})
    }

    AddToCart(e){
        var av=false;
        this.state.objs2.forEach(ee=>{
            if(ee.ref==e.ref){
                av=true
            }
        })
        if(!av) this.setState({objs2:[...this.state.objs2,e],total:this.state.total+e.price,stock:this.state.stock+1})
    }
    
    Undo(e){
        var [objs_2,some] = [this.state.objs2.filter((ee)=>ee.ref!==e.ref),0]
        objs_2.forEach(e=>some=some+e.price)
        this.setState({objs2:objs_2,total:some,stock:objs_2.length,shop:objs_2.length===0?0:1})
    }

    ShopCard(){
        return(
            <div className='shopCardP'>
                <div className='closeP' onClick={()=>{this.setState({shop:0})}}>
                    <img src="icon/close.png" alt=''/>
                </div>
                <div className='text-light'>
                    Total : {this.state.total}$
                </div>
                <div className='shopCard'>
                    {this.state.objs2.map((e,i)=>
                    <div key={i} className='py-1 d-flex align-items-center justify-centent-evenly'>
                        <img style={{height:'20%'}} className='col-3' src={e.url} alt=''/>
                        <div style={{backgroundColor:'#1B1C20'}} className='col-9 py-3'>
                            <p className='text-center text-light'>{e.name}</p>
                            <p className='text-center text-light'>{e.price}$</p>
                        
                            <div className="form-floating my-2 mx-auto col-7">
                                <input className="form-control border border-dark" value='1' type='number' placeholder="name@example.com"/>
                                <label>Quantite</label>
                            </div>
                        
                            <input onClick={()=>{this.Undo(e)}} value='UNDO ' type='button' className='btn btn-danger text-light col-7 d-block mx-auto'/>
                        </div>
                    </div>
                    )}
                    {this.state.total>0 && <input value='Buy' type='button' className='mt-3 col-8 d-block mx-auto btn btn-success'/>}
                </div>
            </div>
        )
    }

    render(){
        return(
            <div className='container d-flex flex-column align-items-center mt-4'>
                <header className='d-flex col-12 align-items-center justify-content-between py-2'>
                    {this.props.nav.map((e,i)=>
                        <a onClick={()=>this.changeState(e)} s key={i} className='nav-link text-light col-2'>{e}</a>
                    )}
                    <div className='shopIcon'>
                        <img onClick={()=>this.state.stock>=1&&this.setState({shop:1})} style={{height:'40px',cursor:'pointer'}} src='icon/SC.png' alt=''/>
                        <span>{this.state.stock}</span>
                    </div>
                </header>
                <div className="form-floating my-3 mx-auto col-10 d-flex">
                    <input onChange={(e)=>{this.Search(e)}} className="form-control border border-dark" placeholder="name@example.com"/>
                    <label>Movie Name</label>
                    <button className="btn btn-primary mx-1 col-1">Search</button>
                </div>
                <div className='col-12 d-flex justify-content-evenly flex-wrap'>
                    {this.state.objs.map((e,i)=>
                        <div style={{width:'400px'}} key={i} className='cadre py-3 col-lg-4 col-md-6 col-sm-12 d-flex flex-column align-items-center justify-centent-center'>
                            <img style={{height:'400px'}} className='col-9' src={e.url} alt=''/>
                            <div style={{backgroundColor:'#1B1C20'}} className='col-9 pb-3'>
                                <p className='text-center text-light my-2'>{e.name}</p>
                                <p className='text-center text-light my-2'>{e.price}$</p>
                                <input onClick={()=>{this.AddToCart({...e})}} value='ADD To Cart' type='button' className='col-6 d-block mx-auto btn btn-primary'/>
                            </div>
                        </div>
                    )}
                </div>
                {this.state.shop===1 && (this.state.stock>0 && this.ShopCard())}
            </div>
    )}
}
export default Games;
